mapper.insert:
BEGIN INSERT INTO chatRoom VALUES(#{chat_seq}, #{one_mem_id}, #{two_mem_id}, #{one_store_nickname}, #{two_store_nickname}, #{one_store_img}, #{two_store_img}, SYSDATE);
INSERT INTO chatList VALUES(#{one_mem_id}, #{chat_seq}, #{two_store_nickname}, #{two_store_img}, #{last_message}, SYSDATE);
INSERT INTO chatList VALUES(#{two_mem_id}, #{chat_seq}, #{one_store_nickname}, #{one_store_img}, #{last_message}, SYSDATE); END;
