mapper.update:
 		update members set mem_name = #{mem_name}
	 						, mem_pwd = #{mem_pwd}
	 						, mem_email = #{mem_email}
	 						, mem_tel = #{mem_tel}
	 						, mem_postcode = #{mem_postcode}
	 						, mem_add1 = #{mem_add1}
	 						, mem_add2 = #{mem_add2}
 		where mem_id = #{mem_id}

mapper.delete:
 		begin

 			insert into withdraw values
 						(#{mem_id}
 						,#{withdraw_lowFrequency}, #{withdraw_rejoin}
 						,#{withdraw_lowContents}, #{withdraw_protectInfo}
 						,#{withdraw_lowBenefit}, #{withdraw_others}
 						,#{withdraw_detailReason}, sysdate );

			UPDATE members SET 
						mem_postcode = 0
						,mem_recentPostcode = 0
						, mem_name='out' 
						, mem_pwd=#{mem_pwd}
						, mem_email=#{mem_email}
						, mem_add1='out' 
						, mem_add2='out' 
						, mem_recentAdd1='out' 
						, mem_recentAdd2='out' 
						, mem_tel='out' 
						, mem_location='out' 
						, mem_recentLocation='out' 
						, mem_agree ='out' 
				WHERE mem_id = #{mem_id};
 		end;

mapper.insert:
		begin
			insert into complain values(#{mem_id}, #{reporter_id}
										,complain_seq.nextval
										, #{complain_category}
										, #{product_seq}
										, #{board_seq}
										, #{review_seq}
										, #{comment_seq}
										, #{complain_content}
										, sysdate
 									);
			update members SET mem_complain = (mem_complain+1) where mem_id = #{mem_id};
		end;
